mapper.select:
select r.id, r.department, d.name, r.create_time, r.status
from reservation r left join doctor d
on r.doctor_id = d.id


        and r.patient_id = #{patientId}


        and r.department like concat('%', #{department}, '%')


        and r.status = #{status}

select r.id, p.name as name, r.create_time
from reservation r left join patient p
on r.patient_id = p.id



        and p.name like concat('%',#{patientName},'%')


        and r.create_time <= #{maxCreateTime}


        and r.create_time >= #{minCreateTime}


        and r.status = 0 and r.doctor_id = #{id}

select r.id, p.name as name, r.create_time, r.status
from reservation r left join patient p
on r.patient_id = p.id


        and r.doctor_id = #{id}


        and p.name like concat('%', #{patientName}, '%')


        and r.create_time <= #{maxCreateTime}


        and r.create_time >= #{minCreateTime}


        and status = #{status}
